import 'dart:developer';\n\n/// Helper class to track and optimize trending songs loading\nclass TrendingSongsPreloader {\n  static final Set<String> _loadedSections = {};\n  \n  /// Mark a section as loaded/visible\n  static void markSectionLoaded(String sectionName) {\n    if (!_loadedSections.contains(sectionName)) {\n      _loadedSections.add(sectionName);\n      log('Trending section loaded: $sectionName', name: 'TrendingPreloader');\n    }\n  }\n  \n  /// Check if a section has been loaded\n  static bool isSectionLoaded(String sectionName) {\n    return _loadedSections.contains(sectionName);\n  }\n  \n  /// Clear cache when user navigates away\n  static void clearCache() {\n    _loadedSections.clear();\n    log('Cleared trending sections cache', name: 'TrendingPreloader');\n  }\n}\n
